'use strict'

const config = require('../config')
// const store = require('../store')

// ajax request that posts a token (which was generated by stripe in the
// checkouts.events.js) to OUR API
const createToken = function (data) {
  return $.ajax({
    url: config.apiUrl + '/tokens',
    method: 'POST',
    data
  })
}

module.exports = {
  createToken
}
